CREATE TABLE `node_red_credentials` (
                                        `CredentialID` int(11) NOT NULL AUTO_INCREMENT,
                                        `JsonData` longtext DEFAULT NULL,
                                        PRIMARY KEY (`CredentialID`)
) ENGINE=InnoDB ;
CREATE TABLE `node_red_flows` (
                                  `FlowID` int(11) NOT NULL AUTO_INCREMENT,
                                  `JsonData` longtext NOT NULL,
                                  PRIMARY KEY (`FlowID`)
) ENGINE=InnoDB ;
CREATE TABLE `node_red_library` (
                                    `LibraryID` int(11) NOT NULL AUTO_INCREMENT,
                                    `Type` varchar(10) DEFAULT NULL,
                                    `Path` longtext DEFAULT NULL,
                                    `Meta` longtext DEFAULT NULL,
                                    `JsonData` longtext DEFAULT NULL,
                                    PRIMARY KEY (`LibraryID`)
) ENGINE=InnoDB ;
CREATE TABLE `node_red_sessions` (
                                     `SessionID` int(11) NOT NULL AUTO_INCREMENT,
                                     `JsonData` longtext DEFAULT NULL,
                                     PRIMARY KEY (`SessionID`)
) ENGINE=InnoDB ;
CREATE TABLE `node_red_settings` (
                                     `SettingID` int(11) NOT NULL AUTO_INCREMENT,
                                     `JsonData` longtext DEFAULT NULL,
                                     PRIMARY KEY (`SettingID`)
) ENGINE=InnoDB ;
