const { DataTypes } = require('sequelize');
module.exports = (sequelize) => {
    const Credentials = sequelize.define("node_red_credentials", {
        CredentialID: {
            type: DataTypes.INTEGER,
            primaryKey: true,
            autoIncrement: true
        },
        JsonData: {
            type: DataTypes.TEXT
        }
    });

    return Credentials;
};
