const { DataTypes } = require('sequelize');
module.exports = (sequelize) => {
    const Flows = sequelize.define("node_red_flows", {
        FlowID: {
            type: DataTypes.INTEGER,
            primaryKey: true,
            autoIncrement: true
        },
        JsonData: {
            type: DataTypes.TEXT
        }
    });

    return Flows;
};
