const { DataTypes } = require('sequelize');
module.exports = (sequelize) => {
    const Libraries = sequelize.define("node_red_libraries", {
        LibraryID: {
            type: DataTypes.INTEGER,
            primaryKey: true,
            autoIncrement: true
        },
        Type: {
            type: DataTypes.STRING(10)
        },
        Path: {
            type: DataTypes.TEXT
        },
        Meta: {
            type: DataTypes.TEXT
        },
        JsonDatas: {
            type: DataTypes.TEXT
        }
    });

    return Libraries;
};
