const { DataTypes } = require('sequelize');
module.exports = (sequelize) => {
    const Sessions = sequelize.define("node_red_sessions", {
        SessionID: {
            type: DataTypes.INTEGER,
            primaryKey: true,
            autoIncrement: true
        },
        JsonData: {
            type: DataTypes.TEXT
        }
    });

    return Sessions;
};
