const { DataTypes } = require('sequelize');
module.exports = (sequelize) => {
    const Settings = sequelize.define("node_red_settings", {
        SettingID: {
            type: DataTypes.INTEGER,
            primaryKey: true,
            autoIncrement: true
        },
        JsonData: {
            type: DataTypes.TEXT
        }
    });

    return Settings;
};
